import React, { useEffect, useState } from "react";
import TinderCard from "react-tinder-card";
import database from './firebase';
import './TinderCards.css';

function TinderCards(){
    const[people,setPeople] = useState([
        {
            name: 'elena',
            url: 'https://animeintroduction.com/wp-content/uploads/2020/10/%E9%AD%94%E5%A5%B3%E3%81%AE%E6%97%85%E3%80%85%EF%BC%93%E8%A9%B1-105-1.jpg',
        },
        {
            name: 'elena',
            url: 'https://livedoor.blogimg.jp/mrbloodman3480-nzsmoet5/imgs/c/8/c8d0d87c.jpg',
        },
        {
            name:'INU',
            url:'https://images-na.ssl-images-amazon.com/images/I/51cc84hBwRL._SX331_BO1,204,203,200_.jpg',
        }
    ]);

    //Piece of code which runs based on a condition
    useEffect(() => {
    //this is where the code runs...
    database.collection('people').onSnapshot()

    }, []);
    // thiws will run ONCE when the component loads, and never again



    //normal way
    // const people = [];
    //people.push('sonny', 'qazi')

    //react way
    // setPeople([...people,'sonny','qazi'])

    return(
        <div>
            <h1>世界一かわいい嫁</h1>
            <div className="tinderCards_cardContainer">
            {people.map(person =>(
                <TinderCard
                className="swipe"
                key={person.name}
                preventSwipe={['up', 'down']}
                >
                    <div 
                    style={{backgroundImage: `url(${person.url})`}}
                    className="card"
                >
                        <h3>{person.name}</h3>
                    </div>
                </TinderCard>
            ))}
            </div>
        </div>
    );

}
export default TinderCards;
