import React, { useState } from "react";
import TinderCard from "react-tinder-card";
import './TinderCards.css';

function TinderCards(){
    const[people,setPeople] = useState([
        {
            name: 'hayato',
            url: 'https://post.greatist.com/wp-content/uploads/sites/3/2020/02/322868_1100-1100x628.jpg',
        },
        {
            name:'hitomi',
            url:'https://images-na.ssl-images-amazon.com/images/I/51cc84hBwRL._SX331_BO1,204,203,200_.jpg',
        }
    ]);
    //normal way
    // const people = [];
    //people.push('sonny', 'qazi')

    //react way
    // setPeople([...people,'sonny','qazi'])

    return(
        <div>
            <h1>世界一かわいい嫁</h1>
            <div className="tinderCards_cardContainer">
            {people.map(person =>(
                <TinderCard
                className="swipe"
                key={person.name}
                preventSwipe={['up', 'down']}
                >
                    <div 
                    style={{backgroundImage: `url(${person.url})`}}
                    className="card"
                >
                        <h3>{person.name}</h3>
                    </div>
                </TinderCard>
            ))}
            </div>
        </div>
    );

}
export default TinderCards;
